select amount metric,case when amount=0 then 0 else 2 end value from (SELECT count(*) amount FROM dba_sys_privs WHERE PRIVILEGE like '%ANY%' and grantee not in ('DBA', 'DBSNMP', 'SYS', 'SYSTEM', 'SYSMAN', 'AQ_ADMINISTRATOR_ROLE', 'OLAP_DBA', 'DATAPUMP_IMP_FULL_DATABASE', 'IMP_FULL_DATABASE', 'EXP_FULL_DATABASE', 'SCHEDULER_ADMIN', 'CAPACITY', 'OEM_MONITOR', 'CTRM', 'OWB$CLIENT', 'CFREITAS', 'AVUSER') and grantee not in (SELECT username FROM dba_users WHERE account_status<>'OPEN') and grantee || privilege not in (SELECT grantee || privilege FROM dba_sys_privs WHERE (grantee='JAVADEBUGPRIV' and PRIVILEGE='DEBUG ANY PROCEDURE') or (grantee='OUTLN' and PRIVILEGE='EXECUTE ANY PROCEDURE')));
exit
